import React, {Component} from 'react';
import "./Cart.css"
import {Link} from "react-router-dom";
import logo from "../images/ezgif-1-e382b6df9dbb.png";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import userService from "../../common/services/User/UserService";
import UserStoreService from "../../common/services/User/UserStoreService";
import Login from "../Authentication/Login/Login";
import Modal from "@material-ui/core/Modal/Modal";

class Cart extends Component {

    state = {

        userStatus: UserStoreService.isLoggedin()? 'Order History': 'Login',
        open: false,

    };

componentDidMount() {

    // let req_headers = {Authorization: 'Bearer ' + UserStoreService.getToken()}
    // let body = {
    //    headers: req_headers,
    // };

    if(UserStoreService.getToken() !== undefined) {
        userService.userInfo().then((data) => {
            // userStoreService.setToken(data.token);
            console.log(data, "data");

        }).catch((error) => {
            alert(error.message);
        });

    }
}

    handleOpen = () => {
        if (this.state.userStatus === 'Login') {
            this.setState({open: true});
        } else {
            this.props.history.push('/userprofile')
        }

    };

    handleClose = (login = false) => {
        let userStatus = 'Login';
        if (login) {
            userStatus = 'Order History';
        }
        this.setState({
            userStatus,
            open: false
        });
    };
    render() {
        return (
            <div>
                <img  width="300" height="200" className="rounded mx-auto d-block logo" src={logo}/>

                {/*<Navbar align="right" bg="white" variant="light">*/}
                    {/*<Nav className="float-right">*/}
                        {/*/!*<Nav.Link onClick={this.handleOpen}>{this.state.userStatus}</Nav.Link>*!/*/}
                        {/*/!*<Nav.Link onClick={() => {this.props.history.push('/cart')}}>Shopping Cart</Nav.Link>*!/*/}
                        {/*/!*<Nav.Link onClick={() => {this.props.history.push('/cart')}}>About Fangnerdo</Nav.Link>*!/*/}

                        {/*<ul>*/}
                            {/*<li><a  href="http://localhost:3000/">Home</a></li>*/}
                            {/*<li><a  href="login">Login</a></li>*/}
                            {/*<li><a  className="active" href="Cart">Shopping Cart</a></li>*/}
                            {/*<li><a href="About">About Us</a></li>*/}

                        {/*</ul>*/}
                    {/*</Nav>*/}
                {/*</Navbar>*/}
                <Navbar align="right" bg="white" variant="light">
                    <Nav className="float-right">

                        <ul>
                            <li><a onClick={() => this.props.history.push('/')}>Home</a></li>
                            <li><a onClick={this.handleOpen}>{this.state.userStatus}</a></li>
                            <li><a onClick={() => this.props.history.push('/cart')}>Shopping Cart</a></li>
                            <li><a onClick={() => this.props.history.push('/about')}>About Us</a></li>

                        </ul>
                    </Nav>
                </Navbar>
                <Modal
                    aria-labelledby="sm snoimple-modal-title"
                    aria-describedby="simple-modal-description"
                    open={this.state.open}
                    onClose={() => this.handleClose(false)}
                >
                    <Login closeModal={this.handleClose}/>
                </Modal>
                <div className="container">

                    <div className="row">
                        <div className="col-xs-8">
                            <div className="panel panel-info">
                                <div className="panel-heading">
                                    <div className="panel-title">
                                        <div className="row padButtom">
                                            <div className="col-xs-6">
                                                <h5><span className="glyphicon glyphicon-shopping-cart"/> Shopping
                                                    Cart</h5>
                                            </div>

                                        </div>
                                    </div>
                                </div>
                                <div className="panel-body">
                                    <div className="row">
                                        <div className="col-xs-2"><img className="img-responsive"
                                                                       src="http://placehold.it/100x70"/>
                                        </div>
                                        <div className="col-xs-4 pad2">
                                            <h4 className="product-name"><strong>Product nameProduct nameProduct nameProduct nameProduct nameProduct nameProduct nameProduct nameProduct name</strong></h4>
                                            <h4>
                                                <small>Product description</small>
                                            </h4>
                                        </div>
                                        <div className="col-xs-6">
                                            <div className="col-xs-6 text-right">
                                                <h6><strong>25.00 <span className="text-muted">x</span></strong></h6>
                                            </div>
                                            <div className="col-xs-4 pad">
                                                <input type="text" className="form-control input-sm" defaultValue="1"/>
                                            </div>
                                            <div className="col-xs-2">
                                                <button type="button" className="btn btn-link btn-xs">
                                                    <span className="glyphicon glyphicon-trash"> </span>
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                    <hr/>
                                    <div className="row">
                                        <div className="col-xs-2"><img className="img-responsive"
                                                                       src="http://placehold.it/100x70"/>
                                        </div>
                                        <div className="col-xs-4 pad2">
                                            <h4 className="product-name"><strong>Product name</strong></h4>
                                            <h4>
                                                <small>Product description</small>
                                            </h4>
                                        </div>
                                        <div className="col-xs-6">
                                            <div className="col-xs-6 text-right">
                                                <h6><strong>25.00 <span className="text-muted">x</span></strong>
                                                </h6>
                                            </div>
                                            <div className="col-xs-4 pad">
                                                <input type="text" className="form-control input-sm" defaultValue="1"/>
                                            </div>
                                            <div className="col-xs-2">
                                                <button type="button" className="btn btn-link btn-xs">
                                                    <span className="glyphicon glyphicon-trash"> </span>
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                    <hr/>

                                </div>
                                <div className="panel-footer">
                                    <div className="row text-center">
                                        <div className="col-xs-9">
                                            <h4 className="text-right">Total <strong>$50.00</strong></h4>
                                        </div>
                                        <div className="col-xs-3 pad1">
                                            <Link to="/checkout">
                                                <button type="button" className="btn btn-success btn-block">
                                                    Checkout
                                                </button>
                                            </Link>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Cart;
