import React, {Component} from 'react';
import UserStoreService from "../../../common/services/User/UserStoreService";
import userService from "../../../common/services/User/UserService";


class HistoryBox extends Component {

    state = {

        orderHistoryDetail: [],

    };


    // componentDidMount() {
    //
    //     let body = {
    //         authorization: UserStoreService.getToken(),
    //         orderid: this.props.orderId,
    //     };
    //     console.log(body.authorization, "token")
    //     userService.getOrderHistoryDetail(JSON.stringify(body)).then((data) => {
    //         console.log(data);
    //
    //         this.setState({orderHistoryDetail: data});
    //         console.log(this.state.orderHistoryDetail,"orderHistory")
    //     }).catch((error) => {
    //         alert(error.message);
    //     });
    //
    // }

    render() {
        return (
            <div>
                <div className="bg">
                    <div>
                        <div >
                            <p className="borderexample"></p>
                            <div className="text-muted m-b-30 freeFont">

                                OrderId: {this.state.orderId}
                            </div>
                            <div className="text-muted m-b-30 freeFont">
                                OrderStatus: {this.state.orderStatus}
                            </div>
                            <div className="text-muted m-b-30 freeFont">
                                Date: {this.state.date}
                            </div>
                            <div className="text-muted m-b-30 freeFont">
                                TotalPrice: {this.state.totalPrice} </div>
                        </div>

                        <div className="row clearfix">
                            <div className="col-md-12 column">
                                <table
                                    className="table table-bordered table-hover"
                                    id="tab_logic"
                                >
                                    <thead>
                                    <tr>

                                        <th className="text-center freeFont" > Picture </th>
                                        <th className="text-center freeFont"> Name </th>
                                        <th className="text-center freeFont"> Price </th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    {this.state.rows.map((item, idx) => (
                                        <tr id="addr0" key={idx}>

                                            <td>
                                                <img className="rounded managerpicsize imagesize " src= {this.state.pic[idx]} />
                                            </td>
                                            <td>
                                                <p className="fontsize" >{this.state.ordername[idx]}</p>
                                            </td>
                                            <td>
                                                <p className="fontsize" > {this.state.price[idx]}</p>
                                            </td>

                                        </tr>
                                    ))}
                                    </tbody>
                                </table>


                            </div>
                        </div>

                    </div>
                </div>

            </div>
        )

    };
}
export default HistoryBox;
